import React, { useEffect, useState } from 'react';
import "./Dashboard.css";
import axios from 'axios';

function Dashboard({ onLogout }) {
  const username = localStorage.getItem('username') || 'Student';
  const userId = localStorage.getItem('userId'); // ✅ Get from Auth.js login
  const token = localStorage.getItem('token');
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    if (userId && token) {
      axios
        .get(`http://localhost:5000/api/exam/status/${userId}`, {
          headers: { Authorization: `Bearer ${token}` }
        })
        .then(res => {
          setSubmitted(res.data.submitted);
          localStorage.setItem(`${username}_examSubmitted`, res.data.submitted);
        })
        .catch(err => {
          console.error("Error fetching exam status:", err);
        });
    }
  }, [userId, token, username]);

  const handleBeginExam = () => {
    localStorage.removeItem(`${username}_examSubmitted`);
    window.location.hash = '#/exam';
  };

  return (
    <div className="dashboard-container">
      <nav className="dashboard-navbar">
        <span className="dashboard-title">Exam Portal</span>
        <div className="dashboard-user">
          <span className="dashboard-username">{username}</span>
          <button className="dashboard-signout" onClick={onLogout}>Log Out</button>
        </div>
      </nav>

      <div className="dashboard-content">
        <h2>Welcome, {username}!</h2>
        <h3>Ready to take your exam?</h3>
        <p>Click the button below to start your exam session.</p>

        <div style={{ margin: '16px 0', padding: 12, background: '#eef', borderRadius: 6 }}>
          <strong>Exam Details</strong><br />
          Duration: 30 minutes<br />
          Questions: 30 multiple choice<br />
          One-time submission only
        </div>

        <div style={{ margin: '16px 0', padding: 12, background: '#ffe', borderRadius: 6 }}>
          <strong>Instructions</strong>
          <ul>
            <li>Read each question carefully</li>
            <li>Select only one answer per question</li>
            <li>Use Next/Previous to navigate</li>
            <li>Submit before finishing</li>
          </ul>
        </div>

        {!submitted ? (
          <button
            style={{
              padding: 10,
              background: '#1976d2',
              color: 'white',
              border: 'none',
              borderRadius: 6,
              cursor: 'pointer'
            }}
            onClick={handleBeginExam}
          >
            Begin Exam
          </button>
        ) : (
          <button
            style={{
              padding: 10,
              background: '#43cea2',
              color: 'white',
              border: 'none',
              borderRadius: 6,
              cursor: 'pointer'
            }}
            onClick={() => window.location.hash = '#/results'}
          >
            View Results
          </button>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
