import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import { checkExamStatus } from './api';

function Dashboard({ onLogout, submitted }) {
  const username = localStorage.getItem('username') || 'Student';
  const token = localStorage.getItem('token');
  const [examTaken, setExamTaken] = useState(submitted);

  useEffect(() => {
    if (token) {
      checkExamStatus(token).then(res => setExamTaken(res.data.taken));
    }
  }, [token]);

  return (
    <div className="dashboard-container">
      <nav>
        <span>Exam Portal</span>
        <div>
          <span>{username}</span>
          <button onClick={onLogout}>Logout</button>
        </div>
      </nav>
      <h2>Welcome, {username}!</h2>
      {!examTaken ? (
        <button onClick={() => window.location.hash = '#/exam'}>Begin Exam</button>
      ) : (
        <button onClick={() => window.location.hash = '#/results'}>View Results</button>
      )}
    </div>
  );
}

export default Dashboard;
