import React, { useState, useEffect } from 'react';
import './App.css';
import Auth from './Auth';
import Exam from './Exam';
import Dashboard from './Dashboard';
import Results from './Results';
import axios from 'axios';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [route, setRoute] = useState(window.location.hash.replace('#/', '') || 'dashboard');
  const [submitted, setSubmitted] = useState(false);
  const [userId, setUserId] = useState(localStorage.getItem('userId') || null);

  useEffect(() => {
    const onHashChange = () => {
      setRoute(window.location.hash.replace('#/', '') || 'dashboard');
    };
    window.addEventListener('hashchange', onHashChange);
    return () => window.removeEventListener('hashchange', onHashChange);
  }, []);

  // Check exam status from backend
  useEffect(() => {
    if (!token || !userId) return;

    axios.get(`http://localhost:5000/api/exam/status/${userId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        setSubmitted(res.data.examStatus);
      })
      .catch(err => {
        console.error("Error fetching exam status:", err);
      });
  }, [token, route, userId]);

  const handleAuth = (t, id) => {
    setToken(t);
    setUserId(id);
    localStorage.setItem('token', t);
    localStorage.setItem('userId', id);
    window.location.hash = '#/dashboard';
  };

  const handleLogout = () => {
    setToken(null);
    setUserId(null);
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    window.location.hash = '#/login';
  };

  if (!token) {
    if (route === 'login' || route === '') return <Auth onAuth={handleAuth} />;
    window.location.hash = '#/login';
    return null;
  }

  if (route === 'dashboard') {
    return <Dashboard onLogout={handleLogout} submitted={submitted} />;
  }
  if (route === 'exam') {
    return <Exam token={token} userId={userId} />;
  }
  if (route === 'results') {
    return <Results token={token} />;
  }

  return <Dashboard onLogout={handleLogout} submitted={submitted} />;
}

export default App;
