import axios from 'axios';
const API_URL = 'http://localhost:5000/api';

export const register = (username, password) =>
  axios.post(`${API_URL}/auth/register`, { username, password });

export const login = (username, password) =>
  axios.post(`${API_URL}/auth/login`, { username, password });

export const getQuestions = (token) =>
  axios.get(`${API_URL}/exam/questions`, { headers: { Authorization: `Bearer ${token}` } });

export const submitExam = (answers, token) =>
  axios.post(`${API_URL}/exam/submit`, { answers }, { headers: { Authorization: `Bearer ${token}` } });

export const getResult = (token) =>
  axios.get(`${API_URL}/exam/result`, { headers: { Authorization: `Bearer ${token}` } });
