import React, { useEffect, useState } from "react";
import { checkExamStatus } from "./api";
import "./Dashboard.css";

function Dashboard({ onLogout, submitted }) {
  const username = localStorage.getItem("username") || "Student";

  const handleBeginExam = () => {
    window.location.hash = "#/exam";
  };

  return (
    <div className="dashboard-container">
      <nav className="dashboard-navbar">
        <span className="dashboard-title">Exam Portal</span>
        <div>
          <span>{username}</span>
          <button onClick={onLogout}>Log Out</button>
        </div>
      </nav>

      <div className="dashboard-content">
        <h2>Welcome, {username}!</h2>
        {!submitted ? (
          <button onClick={handleBeginExam}>Begin Exam</button>
        ) : (
          <button onClick={() => window.location.hash = "#/results"}>View Results</button>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
