import React, { useEffect, useState } from "react";
import { checkExamStatus } from "./api"; // optional for future
import "./Dashboard.css";

function Dashboard({ token, onLogout }) {
  const username = localStorage.getItem("username") || "Student";
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    fetch(`${process.env.REACT_APP_BASE_URL || "http://localhost:5000"}/api/exam/result`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.review?.length > 0) setSubmitted(true);
      });
  }, [token]);

  const handleBeginExam = () => {
    window.location.hash = "#/exam";
  };

  return (
    <div className="dashboard-container">
      <h2>Welcome, {username}!</h2>
      {!submitted ? (
        <button onClick={handleBeginExam}>Begin Exam</button>
      ) : (
        <button onClick={() => (window.location.hash = "#/results")}>
          View Results
        </button>
      )}
      <button onClick={onLogout}>Log Out</button>
    </div>
  );
}

export default Dashboard;
