import React, { useState, useEffect } from "react";
import Auth from "./Auth";
import Dashboard from "./Dashboard";
import Exam from "./Exam";
import Results from "./Results";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [userId, setUserId] = useState(localStorage.getItem("userId") || "");
  const [page, setPage] = useState("login");

  useEffect(() => {
    const handleHashChange = () => {
      const hash = window.location.hash.replace("#/", "");
      
      // **Check if exam is in progress**
      const examQuestions = localStorage.getItem("exam_questions");
      const examAnswers = localStorage.getItem("exam_answers");
      const examTime = localStorage.getItem("exam_timeLeft");

      if (examQuestions && examAnswers && examTime) {
        // Resume exam on refresh
        setPage("exam");
        window.location.hash = "#/exam";
      } else if (hash) {
        setPage(hash);
      } else {
        // Default redirect
        setPage(token ? "dashboard" : "login");
        window.location.hash = token ? "#/dashboard" : "#/login";
      }
    };

    window.addEventListener("hashchange", handleHashChange);
    handleHashChange(); // run on page load
    return () => window.removeEventListener("hashchange", handleHashChange);
  }, [token]);

  const handleAuth = (newToken, newUserId) => {
    localStorage.setItem("token", newToken);
    localStorage.setItem("userId", newUserId);
    setToken(newToken);
    setUserId(newUserId);
    window.location.hash = "#/dashboard"; // redirect after login
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setToken("");
    setUserId("");
    window.location.hash = "#/login";
  };

  if (!token) return <Auth onAuth={handleAuth} />;

  switch (page) {
    case "dashboard":
      return <Dashboard onLogout={handleLogout} />;
    case "exam":
      return <Exam token={token} />;
    case "results":
      return <Results token={token} />;
    default:
      window.location.hash = "#/dashboard";
      return null;
  }
}

export default App;
