import React, { useState, useEffect } from 'react';
import { getQuestions, submitExam, getResult } from './api';

function Exam({ token }) {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [current, setCurrent] = useState(0);
  const [timer, setTimer] = useState(1800); // 30 min
  const [submitted, setSubmitted] = useState(false);
  const [result, setResult] = useState(null);

  useEffect(() => {
    getQuestions(token).then(res => {
      setQuestions(res.data);
      setAnswers(Array(res.data.length).fill(null));
    });
  }, [token]);

  useEffect(() => {
    if (submitted || timer === 0) return;
    const interval = setInterval(() => setTimer(t => t - 1), 1000);
    if (timer === 0) handleSubmit();
    return () => clearInterval(interval);
  }, [timer, submitted]);

  const handleOption = idx => {
    const newAnswers = [...answers];
    newAnswers[current] = idx;
    setAnswers(newAnswers);
  };

  const handleSubmit = () => {
    if (submitted) return;
    const payload = questions.map((q, i) => ({ questionId: q._id, selectedOption: answers[i] }));
    submitExam(payload, token).then(res => {
      setSubmitted(true);
      getResult(token).then(r => setResult(r.data));
    });
  };

  if (!questions.length) return <div>Loading questions...</div>;
  if (submitted && result) return (
    <div className="result-container">
      <h2>Exam Submitted</h2>
      <div>Score: {result.score}</div>
      <div>Submitted At: {new Date(result.submittedAt).toLocaleString()}</div>
      <button onClick={() => window.location.hash = '#/dashboard'}>Return to Dashboard</button>
    </div>
  );

  const q = questions[current];
  return (
    <div className="exam-container">
      <div className="timer">Time Left: {Math.floor(timer/60)}:{('0'+(timer%60)).slice(-2)}</div>
      <h3>Question {current+1} of {questions.length}</h3>
      <div className="question">{q.question}</div>
      <ul>
        {q.options.map((opt, idx) => (
          <li key={idx}>
            <label>
              <input type="radio" checked={answers[current]===idx} onChange={() => handleOption(idx)} />
              {opt}
            </label>
          </li>
        ))}
      </ul>
      <button disabled={current===0} onClick={()=>setCurrent(c=>c-1)}>Previous</button>
      <button disabled={current===questions.length-1} onClick={()=>setCurrent(c=>c+1)}>Next</button>
      <button onClick={handleSubmit}>Submit Exam</button>
    </div>
  );
}

export default Exam;
