// src/api.js
import axios from "axios";

const BASE_URL = "http://localhost:5000/api";

// Auth APIs
export const register = (data) => axios.post(`${BASE_URL}/auth/register`, data);
export const login = (data) => axios.post(`${BASE_URL}/auth/login`, data);

// Exam APIs
export const getQuestions = (token) =>
  axios.get(`${BASE_URL}/exam/questions`, {
    headers: { Authorization: `Bearer ${token}` },
  });

export const submitExam = (answers, token) =>
  axios.post(
    `${BASE_URL}/exam/submit`,
    { answers },
    { headers: { Authorization: `Bearer ${token}` } }
  );

export const getResult = (token) =>
  axios.get(`${BASE_URL}/exam/result`, {
    headers: { Authorization: `Bearer ${token}` },
  });
