// src/api.js
import axios from "axios";

const API_URL = "http://localhost:5000/api";

// ----------------- Auth APIs -----------------
export const login = (username, password) => {
  return axios.post(`${API_URL}/auth/login`, { username, password });
};

export const register = (username, password) => {
  return axios.post(`${API_URL}/auth/register`, { username, password });
};

// ----------------- Exam APIs -----------------
export const getQuestions = (token) => {
  return axios.get(`${API_URL}/exam/questions`, {
    headers: { Authorization: `Bearer ${token}` }
  });
};

export const submitExam = (answers, token) => {
  return axios.post(
    `${API_URL}/exam/submit`,
    { answers },
    { headers: { Authorization: `Bearer ${token}` } }
  );
};

export const getResult = (token) => {
  return axios.get(`${API_URL}/exam/result`, {
    headers: { Authorization: `Bearer ${token}` }
  });
};

export const checkExamStatus = (token) => {
  return axios.get(`${API_URL}/exam/status`, {
    headers: { Authorization: `Bearer ${token}` }
  });
};
