import React, { useState, useEffect } from 'react';
import "./Exam.css";
import { getQuestions, submitExam, getResult } from './api';

function Exam({ token }) {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [current, setCurrent] = useState(0);
  const [submitted, setSubmitted] = useState(false);
  const [result, setResult] = useState(null);
  const [timeLeft, setTimeLeft] = useState(() => {
    const saved = localStorage.getItem('exam_time_left');
    return saved ? parseInt(saved, 10) : 30 * 60; // 30 minutes default
  });

  // Save current question index whenever it changes
  useEffect(() => {
    localStorage.setItem('exam_current_index', current);
  }, [current]);

  // Save remaining time whenever it changes
  useEffect(() => {
    localStorage.setItem('exam_time_left', timeLeft);
  }, [timeLeft]);

  // Timer effect
  useEffect(() => {
    if (!questions.length || submitted) return;
    if (timeLeft <= 0) {
      handleSubmit();
      return;
    }
    const timer = setTimeout(() => setTimeLeft(t => t - 1), 1000);
    return () => clearTimeout(timer);
  }, [questions.length, submitted, timeLeft]);

  // On mount, check if user already submitted or load saved state
  useEffect(() => {
    getResult(token).then(r => {
      if (r.data && r.data.review) {
        setSubmitted(true);
        setResult(r.data);
      } else {
        getQuestions(token).then(res => {
          setQuestions(res.data);
          setAnswers(Array(res.data.length).fill(null));

          const savedIndex = parseInt(localStorage.getItem('exam_current_index'), 10);
          setCurrent(!isNaN(savedIndex) ? savedIndex : 0);
        });
      }
    }).catch(() => {
      getQuestions(token).then(res => {
        setQuestions(res.data);
        setAnswers(Array(res.data.length).fill(null));

        const savedIndex = parseInt(localStorage.getItem('exam_current_index'), 10);
        setCurrent(!isNaN(savedIndex) ? savedIndex : 0);
      });
    });
  }, [token]);

  const handleOption = idx => {
    const newAnswers = [...answers];
    newAnswers[current] = idx;
    setAnswers(newAnswers);
  };

  const handleSubmit = () => {
    if (submitted) return;

    const payload = questions.map((q, i) => ({
      questionId: q._id,
      selectedOption: answers[i]
    }));

    submitExam(payload, token).then(() => {
      setSubmitted(true);
      localStorage.removeItem('exam_current_index');
      localStorage.removeItem('exam_time_left');
      getResult(token).then(r => {
        setResult(r.data);
        window.location.hash = '#/results';
      });
    });
  };

  // Results Page
  if (submitted && result) {
    const total = result.review ? result.review.length : 0;
    const correct = result.review ? result.review.filter(q => q.correct === q.selected).length : 0;
    const wrong = total - correct;
    const percent = total ? Math.round((correct / total) * 100) : 0;

    return (
      <div className="result-container card">
        <h2>Exam Submitted</h2>
        <div style={{ fontSize: '1.2em', margin: '12px 0' }}>
          <strong>Score:</strong> {result.score} / {total}
        </div>
        <div><strong>Correct:</strong> {correct}</div>
        <div><strong>Wrong:</strong> {wrong}</div>
        <div><strong>Percentage:</strong> {percent}%</div>
        <div>Submitted At: {new Date(result.submittedAt).toLocaleString()}</div>

        <h3 style={{ marginTop: 24 }}>Review Your Answers</h3>
        <div style={{ textAlign: 'left', margin: '0 auto', maxWidth: 500 }}>
          {result.review && result.review.map((q, idx) => {
            const isCorrect = q.correct === q.selected;
            return (
              <div key={idx} style={{
                marginBottom: 24,
                padding: 16,
                borderRadius: 10,
                background: '#f7f7fa',
                boxShadow: '0 2px 8px rgba(67,206,162,0.08)'
              }}>
                <div style={{ fontWeight: 600, marginBottom: 8 }}>{idx + 1}. {q.question}</div>
                <div style={{ marginBottom: 8 }}>
                  <span style={{ color: isCorrect ? '#43cea2' : '#d32f2f', fontWeight: 600 }}>
                    {isCorrect ? 'Correct' : 'Wrong'}
                  </span>
                </div>
                <ul style={{ listStyle: 'none', padding: 0 }}>
                  {q.options.map((opt, i) => (
                    <li key={i} style={{
                      padding: '8px 12px',
                      margin: '6px 0',
                      border: '2px solid',
                      borderColor: q.correct === i ? '#43cea2' : (q.selected === i ? '#d32f2f' : '#ccc'),
                      borderRadius: 6,
                      backgroundColor: q.correct === i ? '#e8f5e9' : (q.selected === i && q.correct !== i ? '#ffebee' : '#fff'),
                      fontWeight: q.correct === i || q.selected === i ? 600 : 400
                    }}>
                      {opt}
                      {q.correct === i ? ' (Correct)' : ''}
                      {q.selected === i && q.correct !== i ? ' (Your answer)' : ''}
                    </li>
                  ))}
                </ul>
              </div>
            );
          })}
        </div>
        <button onClick={() => window.location.hash = '#/dashboard'}>Return to Dashboard</button>
      </div>
    );
  }

  // Loading State
  if (!questions.length) return (
    <div className="exam-container" style={{ textAlign: 'center', padding: '60px 0' }}>
      <h2>Loading questions...</h2>
    </div>
  );

  const q = questions[current];
  const minutes = Math.floor(timeLeft / 60);
  const seconds = timeLeft % 60;

  // Exam Page
  return (
    <div className="exam-container">
      <div className="exam-timer" style={{
        textAlign: 'right',
        fontWeight: '600',
        color: '#d32f2f',
        marginBottom: '16px',
        fontSize: '1.3em'
      }}>
        Time Left: {minutes.toString().padStart(2, '0')}:{seconds.toString().padStart(2, '0')}
      </div>

      <h3>Question {current + 1} of {questions.length}</h3>
      <div className="question" style={{ marginBottom: 16 }}>{q.question}</div>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {q.options.map((opt, idx) => (
          <li
            key={idx}
            style={{
              padding: '8px 12px',
              margin: '6px 0',
              border: '2px solid',
              borderColor: answers[current] === idx ? '#1976d2' : '#ccc',
              borderRadius: 6,
              backgroundColor: answers[current] === idx ? '#e3f2fd' : '#fff',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              fontWeight: answers[current] === idx ? 600 : 400
            }}
            onClick={() => handleOption(idx)}
          >
            <span style={{ fontWeight: 'bold', marginRight: 6 }}>
              {String.fromCharCode(65 + idx)}.
            </span>
            {opt}
          </li>
        ))}
      </ul>

      <div style={{ display: 'flex', gap: '16px', marginTop: '18px' }}>
        <button disabled={current === 0} onClick={() => setCurrent(c => c - 1)}>Previous</button>
        <button disabled={current === questions.length - 1} onClick={() => setCurrent(c => c + 1)}>Next</button>
        <button onClick={handleSubmit}>Submit Exam</button>
      </div>
    </div>
  );
}

export default Exam;
