import React, { useEffect, useState } from 'react';
import { getResult } from './api';
import './Results.css';

function Results({ token }) {
  const [result, setResult] = useState(null);
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    getResult(token).then(r => setResult(r.data));
  }, [token]);

  if (!result) return <div>Loading result...</div>;

  const review = result.review || [];
  const total = review.length;

  return (
    <div className="result-container">
      <h2>Exam Submitted</h2>
      <div>Score: {result.score} / {total}</div>
      {review[current] && <div>
        <div>{current+1}. {review[current].question}</div>
        <div>Correct answer: {review[current].correct}</div>
        <div>Your answer: {review[current].selected}</div>
      </div>}
      <div>
        <button disabled={current===0} onClick={()=>setCurrent(c=>c-1)}>Previous</button>
        <button disabled={current===total-1} onClick={()=>setCurrent(c=>c+1)}>Next</button>
      </div>
      <button onClick={()=>window.location.hash='#/dashboard'}>Return to Dashboard</button>
    </div>
  );
}

export default Results;
