import React, { useEffect, useState } from "react";
import { getResult } from "./api";
import "./Results.css";

function Results({ token }) {
  const [result, setResult] = useState(null);
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    if (!token) return;
    getResult(token)
      .then((r) => {
        console.log("API Result:", r.data); // üëà Debugging log
        setResult(r.data);
      })
      .catch((err) => console.error("Error fetching result:", err));
  }, [token]);

  if (!result) return <div>Loading result...</div>;

  const review = result.review || [];
  const total = review.length;

  const currentQuestion = review[current];

  return (
    <div className="result-container">
      <h2>üéØ Exam Results</h2>
      <div>Your Score: {result.score} / {total}</div>

      {currentQuestion && (
        <div className="bg-white p-6 rounded-lg shadow border border-gray-200 mb-6">
          {/* Question */}
          <div className="border border-gray-300 rounded-md p-4 mb-4 bg-gray-50">
            <h3 className="text-lg font-semibold">
              {current + 1}. {currentQuestion.question}
            </h3>
          </div>

          {/* All options - safe fallback */}
          <div className="space-y-3">
            {(currentQuestion.options || []).map((opt, idx) => {
              let borderClass = "border-gray-300";
              let bgClass = "bg-white";
              let textClass = "text-black";

              if (opt === currentQuestion.correct) {
                borderClass = "border-green-500";
                bgClass = "bg-green-50";
                textClass = "text-green-700";
              }
              if (
                opt === currentQuestion.selected &&
                currentQuestion.selected !== currentQuestion.correct
              ) {
                borderClass = "border-red-500";
                bgClass = "bg-red-50";
                textClass = "text-red-700";
              }

              return (
                <div
                  key={idx}
                  className={`border rounded-md p-3 ${borderClass} ${bgClass} ${textClass}`}
                >
                  {opt}
                </div>
              );
            })}
          </div>
        </div>
      )}

      {/* Navigation buttons */}
      <div className="nav-buttons" style={{ display: "flex", gap: "10px", marginTop: "12px" }}>
        <button disabled={current === 0} onClick={() => setCurrent(c => c - 1)}>Previous</button>
        <button disabled={current === total - 1} onClick={() => setCurrent(c => c + 1)}>Next</button>
      </div>

      <button
        onClick={() => {
          localStorage.removeItem("viewing_results");
          window.location.href = "/#/dashboard";
        }}
        style={{ marginTop: "10px" }}
      >
        Return to Dashboard
      </button>
    </div>
  );
}

export default Results;
