import React, { useState } from 'react';
import './App.css';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Auth from './Auth';
import Exam from './Exam';
import Dashboard from './Dashboard';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const navigate = useNavigate();

  // Always redirect to login on first load if not authenticated
  React.useEffect(() => {
    if (!token && window.location.pathname !== '/login') {
      navigate('/login', { replace: true });
    }
  }, [token, navigate]);

  const handleAuth = t => {
    setToken(t);
    localStorage.setItem('token', t);
    navigate('/dashboard');
  };
  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <Routes>
      <Route path="/login" element={!token ? <Auth onAuth={handleAuth} /> : <Navigate to="/dashboard" />} />
      <Route path="/dashboard" element={token ? <Dashboard onLogout={handleLogout} /> : <Navigate to="/login" />} />
      <Route path="/exam" element={token ? <Exam token={token} /> : <Navigate to="/login" />} />
      <Route path="*" element={<Navigate to={token ? "/dashboard" : "/login"} />} />
    </Routes>
  );
}
export default App;
