import React, { useState, useEffect } from 'react';
import './App.css';
import Auth from './Auth';
import Exam from './Exam';
import Dashboard from './Dashboard';
import Results from './Results';
import { checkExamStatus } from './api';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [route, setRoute] = useState(window.location.hash.replace('#/', '') || 'dashboard');
  const [submitted, setSubmitted] = useState(false);
  const [userId, setUserId] = useState(localStorage.getItem('userId') || null);

  useEffect(() => {
    const onHashChange = () => {
      setRoute(window.location.hash.replace('#/', '') || 'dashboard');
    };
    window.addEventListener('hashchange', onHashChange);
    return () => window.removeEventListener('hashchange', onHashChange);
  }, []);

  useEffect(() => {
    if (!token) return;
    checkExamStatus(token)
      .then(res => setSubmitted(res.data.taken))
      .catch(err => console.error(err));
  }, [token, route]);

  const handleAuth = (t, id) => {
    setToken(t);
    setUserId(id);
    localStorage.setItem('token', t);
    localStorage.setItem('userId', id);
    window.location.hash = '#/dashboard';
  };

  const handleLogout = () => {
    setToken(null);
    setUserId(null);
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    window.location.hash = '#/login';
  };

  if (!token) {
    if (route === 'login' || route === '') return <Auth onAuth={handleAuth} />;
    window.location.hash = '#/login';
    return null;
  }

  switch (route) {
    case 'dashboard':
      return <Dashboard onLogout={handleLogout} submitted={submitted} />;
    case 'exam':
      return <Exam token={token} />;
    case 'results':
      return <Results token={token} />;
    default:
      return <Dashboard onLogout={handleLogout} submitted={submitted} />;
  }
}

export default App;
