// export default Dashboard;
import React, { useEffect, useState } from "react";
import { checkExamStatus } from "./api";

function Dashboard({ onLogout }) {
  const [examStarted, setExamStarted] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    checkExamStatus(token)
      .then((res) => setExamStarted(res.data.started))
      .catch(() => setExamStarted(false));
  }, []);

  const startExam = () => {
    window.location.hash = "#/exam";
  };

  return (
    <div style={{ textAlign: "center", marginTop: 50 }}>
      <h2>Dashboard</h2>
      <button
        onClick={startExam}
        style={{ padding: "10px 20px", margin: 10 }}
        disabled={examStarted}
      >
        {examStarted ? "Exam In Progress" : "Begin Exam"}
      </button>
      <br />
      <button
        onClick={onLogout}
        style={{ padding: "10px 20px", margin: 10, background: "red", color: "white" }}
      >
        Logout
      </button>
    </div>
  );
}

export default Dashboard;
