import React, { useEffect, useState } from 'react';
import "./Dashboard.css";

function Dashboard({ onLogout }) {
  const username = localStorage.getItem('username') || 'Student';
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    // Load persisted status from localStorage
    const status = localStorage.getItem('examSubmitted') === 'true';
    setSubmitted(status);
  }, []);

  return (
    <div className="dashboard-container">
      <nav className="dashboard-navbar">
        <span className="dashboard-title">Exam Portal</span>
        <div className="dashboard-user">
          <span className="dashboard-username">{username}</span>
          <button className="dashboard-signout" onClick={onLogout}>Sign Out</button>
        </div>
      </nav>
      <div className="dashboard-content">
        <h2>Welcome, {username}!</h2>
        <h3>Ready to take your exam?</h3>
        <p>Click the button below to start your exam session.</p>
        <div style={{margin:'16px 0',padding:12,background:'#eef',borderRadius:6}}>
          <strong>Exam Details</strong><br/>
          Duration: 30 minutes<br/>
          Questions: 30 multiple choice<br/>
          One-time submission only
        </div>
        <div style={{margin:'16px 0',padding:12,background:'#ffe',borderRadius:6}}>
          <strong>Instructions</strong>
          <ul>
            <li>Read each question carefully</li>
            <li>Select only one answer per question</li>
            <li>Use Next/Previous to navigate</li>
            <li>Submit before finishing</li>
          </ul>
        </div>
        <h3>Start Your Exam</h3>
        {!submitted ? (
          <button
            style={{fontSize:18,padding:'10px 24px',background:'#1976d2',color:'#fff',border:'none',borderRadius:6,cursor:'pointer',marginRight:'16px'}}
            onClick={() => window.location.hash = '#/exam'}
          >
            Begin Exam
          </button>
        ) : (
          <button
            style={{fontSize:18,padding:'10px 24px',background:'#43cea2',color:'#fff',border:'none',borderRadius:6,cursor:'pointer'}}
            onClick={() => window.location.hash = '#/results'}
          >
            View Results
          </button>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
