import React, { useEffect, useState } from 'react';
import { getResult } from './api';
import './Exam.css'; // ✅ Reuse your exam styling

function Results({ token }) {
  const [result, setResult] = useState(null);
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    if (!token) return;
    getResult(token)
      .then(r => setResult(r.data))
      .catch(err => console.error("Error fetching result:", err));
  }, [token]);

  if (!result) return <div className="exam-container">Loading result...</div>;

  const review = result.review || [];
  const total = review.length;

  const q = review[current];

  // Find indexes
  const correctIdx = q.options.findIndex(opt => opt === q.correct);
  const selectedIdx = q.options.findIndex(opt => opt === q.selected);

  return (
    <div className="exam-container">
      <h2 style={{ textAlign: 'center' }}>🎯 Exam Results</h2>
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        Your Score: {result.score} / {total}
      </div>

      <h3>
        Question {current + 1} of {total}
      </h3>
      <div>{q.question}</div>

      <ul style={{ listStyle: "none", padding: 0 }}>
        {q.options.map((opt, idx) => {
          let border = "2px solid #ccc";
          let bg = "#fff";

          if (idx === correctIdx) {
            border = "2px solid #16a34a"; // green
            bg = "#dcfce7";
          }
          if (selectedIdx === idx && selectedIdx !== correctIdx) {
            border = "2px solid #dc2626"; // red
            bg = "#fee2e2";
          }

          return (
            <li
              key={idx}
              style={{
                padding: "8px",
                margin: "6px 0",
                border,
                borderRadius: 6,
                backgroundColor: bg
              }}
            >
              {String.fromCharCode(65 + idx)}. {opt}
            </li>
          );
        })}
      </ul>

      <div className="exam-actions buttons">
        <button disabled={current === 0} onClick={() => setCurrent(current - 1)}>Previous</button>
        <button disabled={current === total - 1} onClick={() => setCurrent(current + 1)}>Next</button>
        <button
          onClick={() => {
            localStorage.removeItem("viewing_results");
            window.location.href = "/#/dashboard";
          }}
        >
          Return to Dashboard
        </button>
      </div>
    </div>
  );
}

export default Results;
