import React, { useState, useEffect } from 'react';
import './App.css';
import Auth from './Auth';
import Exam from './Exam';
import Dashboard from './Dashboard';
import Results from './Results';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [route, setRoute] = useState(window.location.hash.replace('#/', '') || 'dashboard');
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    const onHashChange = () => setRoute(window.location.hash.replace('#/', '') || 'dashboard');
    window.addEventListener('hashchange', onHashChange);
    return () => window.removeEventListener('hashchange', onHashChange);
  }, []);

  useEffect(() => {
    if (!token) return;
    // Check if exam is submitted
    fetch('http://localhost:5000/api/exam/result', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        if (data && data.review) {
          setSubmitted(true);
          if (route !== 'results') window.location.hash = '#/results';
        } else {
          setSubmitted(false);
        }
      });
  }, [token, route]);

  const handleAuth = t => {
    setToken(t);
    localStorage.setItem('token', t);
    window.location.hash = '#/dashboard';
  };
  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token');
    window.location.hash = '#/login';
  };

  if (!token) {
    if (route === 'login' || route === '') return <Auth onAuth={handleAuth} />;
    window.location.hash = '#/login';
    return null;
  }

  if (submitted) {
    window.location.hash = '#/results';
    return <Results token={token} />;
  }
  if (route === 'dashboard') return <Dashboard onLogout={handleLogout} submitted={submitted} />;
  if (route === 'exam') return <Exam token={token} />;
  if (route === 'results') return <Results token={token} />;
  return <Dashboard onLogout={handleLogout} submitted={submitted} />;
}
export default App;
