import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Dashboard.css";

function Dashboard({ onLogout }) {
  const username = localStorage.getItem("username") || "Student";
  const token = localStorage.getItem("token");
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    if (token) {
      axios
        .get("http://localhost:5000/api/exam/status", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setSubmitted(res.data.taken))
        .catch((err) => console.error("Error fetching exam status:", err));
    }
  }, [token]);

  const handleBeginExam = () => {
    window.location.hash = "#/exam";
  };
const startExam = () => {
  localStorage.setItem("exam_in_progress", "true");
  navigate("/exam"); // or your exam route
};
  return (
    <div className="dashboard-container">
      <nav className="dashboard-navbar">
        <span className="dashboard-title">Exam Portal</span>
        <div className="dashboard-user">
          <span className="dashboard-username">{username}</span>
          <button className="dashboard-signout" onClick={onLogout}>
            Log Out
          </button>
        </div>
      </nav>

      <div className="dashboard-content">
        <h2>Welcome, {username}!</h2>
        {!submitted ? (
          <button onClick={handleBeginExam} style={{ padding: 10, background: "#1976d2", color: "white" }}>
            Begin Exam
          </button>
        ) : (
          <button
            onClick={() => (window.location.hash = "#/results")}
            style={{ padding: 10, background: "#43cea2", color: "white" }}
          >
            View Results
          </button>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
