// export default Exam;
import React, { useState, useEffect } from "react";
import axios from "axios";

function Exam({ token }) {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});

  useEffect(() => {
    // When the exam page loads, mark as in progress
    localStorage.setItem("inExam", "true");

    axios
      .get("http://localhost:5000/api/exam", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setQuestions(res.data.questions);
      })
      .catch((err) => {
        console.error("Error fetching exam questions:", err);
      });
  }, [token]);

  const handleAnswerChange = (qId, value) => {
    setAnswers((prev) => ({
      ...prev,
      [qId]: value,
    }));
  };

  const handleSubmit = () => {
    axios
      .post(
        "http://localhost:5000/api/exam/submit",
        { answers },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then(() => {
        // Exam submitted â†’ clear flag
        localStorage.removeItem("inExam");
        window.location.hash = "#/results";
      })
      .catch((err) => {
        alert("Submit failed, please try again.");
        console.error(err);
      });
  };

  return (
    <div>
      <h1>Exam</h1>
      {questions.map((q) => (
        <div key={q._id}>
          <p>{q.text}</p>
          {q.options.map((opt, i) => (
            <label key={i}>
              <input
                type="radio"
                name={q._id}
                value={opt}
                checked={answers[q._id] === opt}
                onChange={(e) =>
                  handleAnswerChange(q._id, e.target.value)
                }
              />
              {opt}
            </label>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit}>Submit Exam</button>
    </div>
  );
}

export default Exam;
