import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Register API
export const register = (name, email, password) =>
  axios.post(`${API_URL}/auth/register`, { name, email, password });

// Login API
export const login = (email, password) =>
  axios.post(`${API_URL}/auth/login`, { email, password });

// Get Questions
export const getQuestions = (token) =>
  axios.get(`${API_URL}/exam/questions`, {
    headers: { Authorization: `Bearer ${token}` }
  });

// Submit Exam
export const submitExam = (answers, token) =>
  axios.post(`${API_URL}/exam/submit`, { answers }, {
    headers: { Authorization: `Bearer ${token}` }
  });

// Get Result
export const getResult = (token) =>
  axios.get(`${API_URL}/exam/result`, {
    headers: { Authorization: `Bearer ${token}` }
  });
