import axios from "axios";

const API_URL = "http://localhost:5000/api";

// Auth APIs
export const login = (credentials) =>
  axios.post(`${API_URL}/auth/login`, credentials);

export const register = (data) =>
  axios.post(`${API_URL}/auth/register`, data);

// Exam APIs
export const getQuestions = (token) =>
  axios.get(`${API_URL}/exam/questions`, {
    headers: { Authorization: `Bearer ${token}` }
  });

export const submitExam = (answers, token) =>
  axios.post(`${API_URL}/exam/submit`, answers, {
    headers: { Authorization: `Bearer ${token}` }
  });

export const getResult = (token) =>
  axios.get(`${API_URL}/exam/result`, {
    headers: { Authorization: `Bearer ${token}` }
  });

export const checkExamStatus = (token) =>
  axios.get(`${API_URL}/exam/status`, {
    headers: { Authorization: `Bearer ${token}` }
  });
