import React, { useEffect, useState } from 'react';
import "./Dashboard.css";

function Dashboard({ onLogout }) {
  const username = localStorage.getItem('username') || 'Student';
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    const checkSubmitted = () => {
      const userFlag = localStorage.getItem(`${username}_examSubmitted`);
      setSubmitted(userFlag === 'true');
    };

    checkSubmitted();
    window.addEventListener('hashchange', checkSubmitted);
    return () => window.removeEventListener('hashchange', checkSubmitted);
  }, [username]);

  return (
    <div className="dashboard-container">
      <nav className="dashboard-navbar">
        <span className="dashboard-title">Exam Portal</span>
        <div className="dashboard-user">
          <span className="dashboard-username">{username}</span>
          <button className="dashboard-signout" onClick={onLogout}>Sign Out</button>
        </div>
      </nav>

      <div className="dashboard-content">
        <h2>Welcome, {username}!</h2>
        
        <h3>Start Your Exam</h3>
        
        {!submitted ? (
          <button
            style={{padding:10, background:'#1976d2', color:'white', border:'none', borderRadius:6, cursor:'pointer'}}
            onClick={() => window.location.hash = '#/exam'}
          >
            Begin Exam
          </button>
        ) : (
          <button
            style={{padding:10, background:'#43cea2', color:'white', border:'none', borderRadius:6, cursor:'pointer'}}
            onClick={() => window.location.hash = '#/results'}
          >
            View Results
          </button>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
