import React, { useEffect, useState } from "react";
import axios from "axios";
// import "./Dashboard.css"; // You can remove most of this if using Tailwind

function Dashboard({ onLogout }) {
  const username = localStorage.getItem("username") || "Student";
  const token = localStorage.getItem("token");
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    if (token) {
      axios
        .get("http://localhost:5000/api/exam/status", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setSubmitted(res.data.taken))
        .catch((err) => console.error("Error fetching exam status:", err));
    }
  }, [token]);

  const handleBeginExam = () => {
    window.location.href = "/#/exam"; // or navigate("/exam") if using react-router
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navbar */}
      <header className="border-b bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-blue-600">Exam Portal</h1>
          <div className="flex items-center gap-4">
            <span className="text-sm text-gray-600">Welcome, {username}</span>
            <button
              className="px-4 py-2 border rounded hover:bg-gray-100"
              onClick={onLogout}
            >
              Log Out
            </button>
          </div>
        </div>
      </header>

      {/* Main content */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <div className="mb-8">
            <h2 className="text-3xl font-bold text-gray-800 mb-2">
              Ready to take your exam?
            </h2>
            <p className="text-gray-600">
              Click the button below to start your 30-minute exam session.
            </p>
          </div>

          <div className="grid md:grid-cols-2 gap-6 mb-8">
            {/* Exam Details */}
            <div className="bg-white border rounded-lg shadow-sm p-6">
              <h3 className="text-xl font-semibold flex items-center gap-2 text-blue-600 mb-4">
                📋 Exam Details
              </h3>
              <p><strong>Duration:</strong> 30 minutes</p>
              <p><strong>Questions:</strong> 30 multiple choice</p>
              <p><strong>Auto-submit:</strong> When time expires</p>
            </div>

            {/* Instructions */}
            <div className="bg-white border rounded-lg shadow-sm p-6">
              <h3 className="text-xl font-semibold flex items-center gap-2 text-blue-600 mb-4">
                📝 Instructions
              </h3>
              <ul className="list-disc list-inside text-gray-700">
                <li>Read each question carefully</li>
                <li>Select only one answer per question</li>
                <li>Use Next/Previous to navigate</li>
                <li>Submit before time runs out</li>
                <li>Do not refresh or close the tab during the exam</li>
              </ul>
            </div>
          </div>

          {/* Start Exam */}
          <div className="bg-white border rounded-lg shadow-sm p-6 text-center">
            <h3 className="text-xl font-semibold flex justify-center items-center gap-2 text-green-600 mb-2">
              🎯 Start Your Exam
            </h3>
            <p className="text-gray-600 mb-4">
              Once you begin, the timer will start automatically
            </p>
            {!submitted ? (
              <button
                className="px-8 py-4 bg-blue-600 hover:bg-blue-700 text-white font-bold rounded-lg text-lg"
                onClick={handleBeginExam}
              >
                Begin Exam
              </button>
            ) : (
              <button
                className="px-8 py-4 bg-green-600 hover:bg-green-700 text-white font-bold rounded-lg text-lg"
                onClick={() => (window.location.href = "/#/results")}
              >
                View Results
              </button>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default Dashboard;
