import React, { useEffect, useState } from 'react';
import "./Dashboard.css";

function Dashboard({ onLogout }) {
  const username = localStorage.getItem('username') || 'Student';
  const [submitted, setSubmitted] = useState(false);
  const [attemptedCount, setAttemptedCount] = useState(0);
  const [unattemptedCount, setUnattemptedCount] = useState(0);

  useEffect(() => {
    // If user just registered or logged in, clear their examSubmitted flag
    if (username) {
      if (localStorage.getItem('justLoggedIn') === 'true') {
        localStorage.removeItem(`${username}_examSubmitted`);
        localStorage.removeItem(`${username}_answers`); // clear answers
        localStorage.setItem('justLoggedIn', 'false');
      }
    }

    const checkSubmitted = () => {
      const userFlag = localStorage.getItem(`${username}_examSubmitted`);
      setSubmitted(userFlag === 'true');

      // Get attempted/unattempted count from saved answers
      const savedAnswers = JSON.parse(localStorage.getItem(`${username}_answers`)) || {};
      const attempted = Object.keys(savedAnswers).length;
      const totalQuestions = 30; // you can make this dynamic if needed
      const unattempted = totalQuestions - attempted;

      setAttemptedCount(attempted);
      setUnattemptedCount(unattempted);
    };

    checkSubmitted();
    window.addEventListener('hashchange', checkSubmitted);

    return () => window.removeEventListener('hashchange', checkSubmitted);
  }, [username]);

  return (
    <div className="dashboard-container">
      <nav className="dashboard-navbar">
        <span className="dashboard-title">Exam Portal</span>
        <div className="dashboard-user">
          <span className="dashboard-username">{username}</span>
          <button className="dashboard-signout" onClick={onLogout}>Log Out</button>
        </div>
      </nav>

      <div className="dashboard-content">
        <h2>Welcome, {username}!</h2>
        <h3>Ready to take your exam?</h3>
        <p>Click the button below to start your exam session.</p>

        <div style={{margin:'16px 0', padding:12, background:'#eef', borderRadius:6}}>
          <strong>Exam Details</strong><br/>
          Duration: 30 minutes<br/>
          Questions: 30 multiple choice<br/>
          One-time submission only
        </div>

        <div style={{margin:'16px 0', padding:12, background:'#ffe', borderRadius:6}}>
          <strong>Instructions</strong>
          <ul>
            <li>Read each question carefully</li>
            <li>Select only one answer per question</li>
            <li>Use Next/Previous to navigate</li>
            <li>Submit before finishing</li>
          </ul>
        </div>

        

        {!submitted ? (
          <button
            style={{
              padding:10,
              background:'#1976d2',
              color:'white',
              border:'none',
              borderRadius:6,
              cursor:'pointer'
            }}
            onClick={() => window.location.hash = '#/exam'}
          >
            Begin Exam
          </button>
        ) : (
          <button
            style={{
              padding:10,
              background:'#43cea2',
              color:'white',
              border:'none',
              borderRadius:6,
              cursor:'pointer'
            }}
            onClick={() => window.location.hash = '#/results'}
          >
            View Results
          </button>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
