import React, { useEffect, useState } from 'react';
import { getResult } from './api';
import "./Results.css";

function Results({ token }) {
  const [result, setResult] = useState(null);
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    if (!token) return;

    getResult(token)
      .then(res => {
        if (res.data) {
          setResult(res.data);
        }
      })
      .catch(err => {
        console.error("Error fetching results:", err);
      });
  }, [token]);

  if (!result) {
    return <div className="exam-container card" style={{ textAlign: 'center', padding: '60px 0' }}>Loading result...</div>;
  }

  const review = Array.isArray(result.review) ? result.review : [];
  const total = review.length;
  const attempted = review.filter(q => q.selected !== undefined && q.selected !== null).length;
  const unattempted = total - attempted;
  const correct = review.filter(q => q.correct === q.selected).length;
  const wrong = attempted - correct;
  const percent = total ? Math.round((correct / total) * 100) : 0;
  const q = review[current];

  return (
    <div className="result-container card" style={{ maxWidth: '1200px', width: '98vw', margin: '0 auto' }}>
      <h2>Exam Submitted</h2>

      {/* Score and Percentage */}
      <div style={{ fontSize: '1.2em', margin: '12px 0', display: 'flex', justifyContent: 'space-between', maxWidth: 500, gap: '20px' }}>
        <div><strong>Score:</strong> {correct} / {total}</div>
        <div><strong>Percentage:</strong> {percent}%</div>
      </div>

      {/* Stats */}
      <div style={{ display: 'flex', gap: '20px', marginBottom: '12px', flexWrap: 'wrap' }}>
        <div><strong>Correct:</strong> {correct}</div>
        <div><strong>Wrong:</strong> {wrong}</div>
        <div><strong>Unattempted:</strong> {unattempted}</div>
      </div>

      <div>Submitted At: {new Date(result.submittedAt).toLocaleString()}</div>

      {/* Review Section */}
      {q && (
        <>
          <h3 style={{ marginTop: 24 }}>Review Your Answers</h3>
          <div className="review-card" style={{ margin: '0 auto', maxWidth: '900px' }}>
            <div className="review-question">{current + 1}. {q.question}</div>
            <div className="review-status">
              <span style={{
                color: q.correct === q.selected ? '#43cea2' :
                  (q.selected === undefined || q.selected === null ? '#ff0400' : '#d32f2f'),
                fontWeight: 600
              }}>
                {q.correct === q.selected
                  ? 'Correct'
                  : (q.selected === undefined || q.selected === null
                    ? 'Not Answered'
                    : 'Wrong')}
              </span>
            </div>

            {/* Options */}
            <ul className="review-options" style={{ listStyle: 'none', padding: 0 }}>
              {q.options.map((opt, i) => (
                <li key={i} style={{
                  padding: '8px 12px',
                  margin: '6px 0',
                  border: '2px solid',
                  borderColor:
                    q.correct === i
                      ? '#43cea2'
                      : q.selected === i && q.correct !== i
                        ? '#d32f2f'
                        : '#ccc',
                  borderRadius: 6,
                  backgroundColor:
                    q.correct === i
                      ? '#e6f9f2'
                      : q.selected === i && q.correct !== i
                        ? '#fdecea'
                        : '#fff',
                  color:
                    q.correct === i
                      ? '#2e7d32'
                      : q.selected === i && q.correct !== i
                        ? '#c62828'
                        : '#333',
                  fontWeight:
                    q.correct === i || q.selected === i ? 600 : 400
                }}>
                  <span style={{ fontWeight: 'bold', marginRight: 6 }}>
                    {String.fromCharCode(65 + i)}.
                  </span>
                  {opt}
                  {q.correct === i ? ' (Correct)' : ''}
                  {q.selected === i && q.correct !== i ? ' (Your answer)' : ''}
                </li>
              ))}

              {/* No answer case */}
              {(q.selected === undefined || q.selected === null) && (
                <li style={{
                  padding: '8px 12px',
                  margin: '6px 0',
                  border: '2px dashed #aaa',
                  borderRadius: 6,
                  backgroundColor: '#fff8e1',
                  color: '#8d6e63',
                  fontStyle: 'italic'
                }}>
                  You did not select an answer for this question.
                </li>
              )}
            </ul>
          </div>

          {/* Navigation */}
          <div style={{ display: 'flex', justifyContent: 'center', gap: 16, margin: '24px 0' }}>
            <button disabled={current === 0} onClick={() => setCurrent(c => c - 1)}>Previous</button>
            <span>Question {current + 1} of {total}</span>
            <button disabled={current === total - 1} onClick={() => setCurrent(c => c + 1)}>Next</button>
          </div>
        </>
      )}

      {/* Back to Dashboard */}
      <button
        style={{
          padding: '10px 16px',
          background: '#43cea2',
          color: 'white',
          border: 'none',
          borderRadius: 6,
          cursor: 'pointer'
        }}
        onClick={() => window.location.hash = '#/dashboard'}
      >
        Return to Dashboard
      </button>
    </div>
  );
}

export default Results;
