import React, { useState } from 'react';
import './Auth.css';
import { register, login } from './api';

function Auth({ onAuth }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState('');

  const validatePassword = (pwd) => {
    const errors = [];
    if (pwd.length < 8) errors.push('At least 8 characters');
    if (!/[A-Z]/.test(pwd)) errors.push('At least one uppercase letter');
    if (!/[a-z]/.test(pwd)) errors.push('At least one lowercase letter');
    if (!/[0-9]/.test(pwd)) errors.push('At least one number');
    if (!/[!@#$%^&*(),.?":{}|<>]/.test(pwd)) errors.push('At least one special character');
    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // reset error before validating

    if (!isLogin) {
      const pwdErrors = validatePassword(password);
      if (pwdErrors.length > 0) {
        setError('Password must meet requirements: ' + pwdErrors.join(', '));
        return;
      }
    }

    try {
      if (isLogin) {
        const res = await login(username, password);
        localStorage.setItem('username', username);
        onAuth(res.data.token);
      } else {
        await register(username, password);
        localStorage.setItem('username', username);
        // ✅ Only switch to login mode if register is successful, and clear fields/errors
        setUsername('');
        setPassword('');
        setError('');
        setIsLogin(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Error');
    }
  };

  return (
    <div className="auth-container card">
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <p>Welcome! Please {isLogin ? 'login' : 'register'} to continue.</p>
      
      <form onSubmit={handleSubmit} style={{display:'flex',flexDirection:'column',alignItems:'center',gap:'16px',marginTop:'24px'}}>
        <input 
          type="text" 
          placeholder="Username" 
          value={username} 
          onChange={e => setUsername(e.target.value)} 
          required 
          style={{width:'300px',padding:'',fontSize:'16px'}}
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={e => setPassword(e.target.value)} 
          required
          style={{width:'350px',padding:'',fontSize:'16px'}}
        />
        <button type="submit" style={{width:'100px',padding:'12px',fontSize:'18px',margin:'0 auto'}}> {isLogin ? 'Login' : 'Register'} </button>
      </form>

      <div style={{display:'flex',justifyContent:'center',marginTop:'18px'}}>
        <button 
          style={{width:'250px',padding:'12px',fontSize:'18px'}}
          onClick={() => { 
            setIsLogin(!isLogin); 
            setError(''); // clear errors when switching mode
          }}
        >
          {isLogin ? 'Need an account? Register' : 'Have an account? Login'}
        </button>
      </div>

      {error && <div style={{color:'red', marginTop: '10px', textAlign:'center'}}>{error}</div>}
    </div>
  );
}

export default Auth;
