import React, { useEffect, useState } from 'react';
import { getResult } from './api';
import "./Results.css";

function Results({ token }) {
  const [result, setResult] = useState(null);
  const [current, setCurrent] = useState(0);
  const username = localStorage.getItem('username') || 'Student';

  useEffect(() => {
    getResult(token).then(r => setResult(r.data));
  }, [token]);

  const handleReturnToDashboard = () => {
    localStorage.setItem(`${username}_examSubmitted`, 'true');
    window.location.hash = '#/dashboard';
  };

  if (!result) return <div className="exam-container card">Loading result...</div>;

  const total = result.review?.length || 0;
  const correct = result.review?.filter(q => q.correct === q.selected).length || 0;
  const wrong = total - correct;
  const percent = total ? Math.round((correct / total) * 100) : 0;
  const q = result.review?.[current];

  return (
    <div className="result-container card" style={{maxWidth:'1200px', margin:'0 auto'}}>
      <h2>Exam Submitted</h2>
      <div><strong>Score:</strong> {result.score} / {total}</div>
      <div><strong>Correct:</strong> {correct}</div>
      <div><strong>Wrong:</strong> {wrong}</div>
      <div><strong>Percentage:</strong> {percent}%</div>
      <div>Submitted At: {new Date(result.submittedAt).toLocaleString()}</div>

      {q && (
        <div style={{marginTop:20}}>
          <div><strong>Q{current+1}:</strong> {q.question}</div>
          <ul>
            {q.options.map((opt, i) => (
              <li key={i} style={{color: q.correct === i ? 'green' : (q.selected === i ? 'red' : 'black')}}>
                {opt} {q.correct === i ? '(Correct)' : q.selected === i ? '(Your answer)' : ''}
              </li>
            ))}
          </ul>
        </div>
      )}

      <div style={{marginTop:20}}>
        <button disabled={current===0} onClick={() => setCurrent(c => c-1)}>Previous</button>
        <button disabled={current===total-1} onClick={() => setCurrent(c => c+1)}>Next</button>
      </div>

      <button style={{marginTop:20}} onClick={handleReturnToDashboard}>Return to Dashboard</button>
    </div>
  );
}

export default Results;
